class Solution:    def numIslands(self, grid: List[List[str]]) -> int:        m, n = len(grid), len(grid[0])        count = 0        def func(i: int, j: int)->None:            nonlocal count            if i >= m or j >= n: return            if i < 0 or j < 0: return            if grid[i][j] == '0': return            if grid[i][j] == 'V': return            grid[i][j] = 'V'            func(i+1,j)            func(i-1,j)            func(i,j+1)            func(i,j-1)        for i in range(m):            for j in range(n):                if grid[i][j] == '0' or grid[i][j] == 'V': continue                else:                    func(i,j)                    count += 1        return count